mapper.select:
select
id as typeId,type,name,author,tags,times,stars,love,dislike
from interest_type
where id = #{typeId}
limit 1;

select
id as typeId,type,name,author,tags,times,stars,love,dislike
from interest_type
where type = #{type} and name = #{name} and author = #{author}
limit 1;

select id as id, user_id as userId, interest_id as interestId, weight as weight from interest_user_interest where user_id in
#{item}
and interest_id in
#{item}
;

mapper.update:
UPDATE interest_user_interest
set
weight = #{weight}
where id = #{id}
